mapper.select:
SELECT h.inbound_request_id AS inboundRequestId, h.inbound_request_no AS inboundRequestNo, h.request_branch_id AS requestBranchId, h.vendor_name AS vendorName, h.status_code AS statusCode, h.requested_at AS requestedAt, h.requested_by AS requestedBy, h.title AS title, h.approval_doc_ver_id AS approvalDocVerId FROM inbound_request_header h WHERE h.use_yn = 1
AND h.status_code = #{statusCode}
AND h.request_branch_id = #{requestBranchId}
ORDER BY h.inbound_request_id DESC
