mapper.select:
SELECT ph.purchase_id AS purchaseId, ph.purchase_no AS purchaseNo, ph.branch_id AS branchId, b.branch_name AS branchName, ph.status_code AS statusCode, ph.requested_at AS requestedAt, ph.memo AS memo, SUM(pd.quantity) AS totalQuantity, SUM(pd.quantity * pd.unit_price) AS totalAmount FROM purchase_header ph JOIN branch b ON b.branch_id = ph.branch_id LEFT JOIN purchase_detail pd ON pd.purchase_id = ph.purchase_id AND pd.use_yn = 1 LEFT JOIN product p ON p.product_id = pd.product_id
AND ph.use_yn = 1 AND b.use_yn = 1
AND ph.branch_id = #{branchId}
AND ph.status_code = #{statusCode}
AND ( ph.purchase_no LIKE CONCAT('%', #{keyword}, '%') OR b.branch_name LIKE CONCAT('%', #{keyword}, '%') OR p.product_name LIKE CONCAT('%', #{keyword}, '%') )
GROUP BY ph.purchase_id, ph.purchase_no, ph.branch_id, b.branch_name, ph.status_code, ph.requested_at, ph.memo ORDER BY ph.purchase_id DESC

SELECT COUNT(*)
FROM inventory
WHERE use_yn = 1
AND branch_id = #{branchId}
AND product_id = #{productId}
