mapper.select:
WITH branch_sales AS ( SELECT s.branch_id, b.branch_name AS branchName, SUM(s.total_amount) AS totalAmount, COUNT(*) AS saleCount, AVG(s.total_amount) AS avgAmount FROM sales s JOIN branch b ON b.branch_id = s.branch_id WHERE s.use_yn = 1 AND b.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
GROUP BY s.branch_id, b.branch_name ), avg_sales AS ( SELECT AVG(totalAmount) AS overallAvg FROM branch_sales ) SELECT bs.branch_id AS branchId, bs.branchName, bs.totalAmount, bs.saleCount, bs.avgAmount, (bs.totalAmount - av.overallAvg) AS diffAmount, ROUND(((bs.totalAmount - av.overallAvg) / av.overallAvg * 100), 2) AS diffPercent FROM branch_sales bs CROSS JOIN avg_sales av ORDER BY bs.totalAmount DESC

SELECT s.category_code AS categoryCode, s.category_code AS categoryName, SUM(s.total_amount) AS totalAmount, COUNT(*) AS saleCount, AVG(s.total_amount) AS avgAmount FROM sales s WHERE s.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
AND s.category_code = #{categoryCode}
GROUP BY s.category_code ORDER BY totalAmount DESC

WITH branch_expenses AS ( SELECT e.branch_id, b.branch_name AS branchName, SUM(e.amount) AS totalAmount, COUNT(*) AS expenseCount, AVG(e.amount) AS avgAmount FROM expenses e JOIN branch b ON b.branch_id = e.branch_id WHERE e.use_yn = 1 AND b.use_yn = 1 AND e.settlement_flag = TRUE AND DATE(e.expense_at) BETWEEN #{startDate} AND #{endDate}
AND e.branch_id = #{branchId}
GROUP BY e.branch_id, b.branch_name ), avg_expenses AS ( SELECT AVG(totalAmount) AS overallAvg FROM branch_expenses ) SELECT be.branch_id AS branchId, be.branchName, be.totalAmount, be.expenseCount, be.avgAmount, (be.totalAmount - av.overallAvg) AS diffAmount, ROUND(((be.totalAmount - av.overallAvg) / av.overallAvg * 100), 2) AS diffPercent FROM branch_expenses be CROSS JOIN avg_expenses av ORDER BY be.totalAmount DESC

SELECT e.category_code AS categoryCode, e.category_code AS categoryName, SUM(e.amount) AS totalAmount, COUNT(*) AS expenseCount, AVG(e.amount) AS avgAmount FROM expenses e WHERE e.use_yn = 1 AND e.settlement_flag = TRUE AND DATE(e.expense_at) BETWEEN #{startDate} AND #{endDate}
AND e.branch_id = #{branchId}
AND e.category_code = #{categoryCode}
GROUP BY e.category_code ORDER BY totalAmount DESC

SELECT s.sale_id AS saleId, s.sale_no AS saleNo, s.branch_id AS branchId, b.branch_name AS branchName, s.sold_at AS soldAt, s.category_code AS categoryCode, s.total_amount AS totalAmount, s.status_code AS statusCode, CASE WHEN ssm.settlement_id IS NOT NULL THEN TRUE ELSE FALSE END AS settled, ssm.settlement_id AS settlementId FROM sales s JOIN branch b ON b.branch_id = s.branch_id LEFT JOIN settlement_sale_map ssm ON ssm.sale_id = s.sale_id AND ssm.use_yn = 1 WHERE s.use_yn = 1 AND b.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
ORDER BY s.sold_at DESC, s.sale_id DESC
