mapper.select:
WITH branch_sales AS ( SELECT s.branch_id, b.branch_name AS branchName, SUM(s.total_amount) AS totalAmount, COUNT(*) AS saleCount, AVG(s.total_amount) AS avgAmount FROM sales s JOIN branch b ON b.branch_id = s.branch_id WHERE s.use_yn = 1 AND b.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
GROUP BY s.branch_id, b.branch_name ), total_sales AS ( SELECT SUM(totalAmount) AS grandTotal FROM branch_sales ) SELECT bs.branch_id AS branchId, bs.branchName, bs.totalAmount, bs.saleCount, bs.avgAmount, CASE WHEN ts.grandTotal > 0 THEN ROUND((bs.totalAmount / ts.grandTotal * 100), 2) ELSE 0 END AS percentage FROM branch_sales bs CROSS JOIN total_sales ts ORDER BY bs.totalAmount DESC

WITH category_sales AS ( SELECT s.category_code AS categoryCode, s.category_code AS categoryName, SUM(s.total_amount) AS totalAmount, COUNT(*) AS saleCount, AVG(s.total_amount) AS avgAmount FROM sales s WHERE s.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
AND s.category_code = #{categoryCode}
GROUP BY s.category_code ), total_sales AS ( SELECT SUM(totalAmount) AS grandTotal FROM category_sales ) SELECT cs.categoryCode, cs.categoryName, cs.totalAmount, cs.saleCount, cs.avgAmount, CASE WHEN ts.grandTotal > 0 THEN ROUND((cs.totalAmount / ts.grandTotal * 100), 2) ELSE 0 END AS percentage FROM category_sales cs CROSS JOIN total_sales ts ORDER BY cs.totalAmount DESC

WITH branch_expenses AS ( SELECT e.branch_id, b.branch_name AS branchName, SUM(e.amount) AS totalAmount, COUNT(*) AS expenseCount, AVG(e.amount) AS avgAmount FROM expenses e JOIN branch b ON b.branch_id = e.branch_id WHERE e.use_yn = 1 AND b.use_yn = 1 AND e.settlement_flag = TRUE AND DATE(e.expense_at) BETWEEN #{startDate} AND #{endDate}
AND e.branch_id = #{branchId}
GROUP BY e.branch_id, b.branch_name ), total_expenses AS ( SELECT SUM(totalAmount) AS grandTotal FROM branch_expenses ) SELECT be.branch_id AS branchId, be.branchName, be.totalAmount, be.expenseCount, be.avgAmount, CASE WHEN te.grandTotal > 0 THEN ROUND((be.totalAmount / te.grandTotal * 100), 2) ELSE 0 END AS percentage FROM branch_expenses be CROSS JOIN total_expenses te ORDER BY be.totalAmount DESC

WITH category_expenses AS ( SELECT e.category_code AS categoryCode, e.category_code AS categoryName, SUM(e.amount) AS totalAmount, COUNT(*) AS expenseCount, AVG(e.amount) AS avgAmount FROM expenses e WHERE e.use_yn = 1 AND e.settlement_flag = TRUE AND DATE(e.expense_at) BETWEEN #{startDate} AND #{endDate}
AND e.branch_id = #{branchId}
AND e.category_code = #{categoryCode}
GROUP BY e.category_code ), total_expenses AS ( SELECT SUM(totalAmount) AS grandTotal FROM category_expenses ) SELECT ce.categoryCode, ce.categoryName, ce.totalAmount, ce.expenseCount, ce.avgAmount, CASE WHEN te.grandTotal > 0 THEN ROUND((ce.totalAmount / te.grandTotal * 100), 2) ELSE 0 END AS percentage FROM category_expenses ce CROSS JOIN total_expenses te ORDER BY ce.totalAmount DESC

SELECT s.sale_id AS saleId, s.sale_no AS saleNo, s.branch_id AS branchId, b.branch_name AS branchName, s.sold_at AS soldAt, s.category_code AS categoryCode, s.total_amount AS totalAmount, s.status_code AS statusCode, CASE WHEN ssm.settlement_id IS NOT NULL THEN TRUE ELSE FALSE END AS settled, ssm.settlement_id AS settlementId FROM sales s LEFT JOIN branch b ON b.branch_id = s.branch_id LEFT JOIN settlement_sale_map ssm ON ssm.sale_id = s.sale_id AND ssm.use_yn = 1 WHERE s.use_yn = 1 AND s.status_code = 'COMPLETED' AND DATE(s.sold_at) BETWEEN #{startDate} AND #{endDate}
AND s.branch_id = #{branchId}
ORDER BY s.sold_at DESC, s.sale_id DESC

SELECT e.expense_id AS expenseId, e.branch_id AS branchId, b.branch_name AS branchName, e.expense_at AS expenseAt, e.category_code AS categoryCode, e.amount AS amount, e.description AS description, e.settlement_flag AS settlementFlag, CASE WHEN sem.settlement_id IS NOT NULL THEN TRUE ELSE FALSE END AS settled, sem.settlement_id AS settlementId FROM expenses e LEFT JOIN branch b ON b.branch_id = e.branch_id LEFT JOIN settlement_expense_map sem ON sem.expense_id = e.expense_id AND sem.use_yn = 1 WHERE e.use_yn = 1 AND e.settlement_flag = TRUE AND DATE(e.expense_at) BETWEEN #{startDate} AND #{endDate}
AND e.branch_id = #{branchId}
ORDER BY e.expense_at DESC, e.expense_id DESC
